<?phpif (!defined('BASEPATH'))  exit('No direct script access allowed');  class Template {    var $CI;    public function __construct() {      // Set the super object to a local variable for use later      $this->CI = & get_instance();    }    public function frontbase($data) {      $template['header'] = $this->CI->load->view('web/layout/header', $data, TRUE);            $template['navigation'] = $this->CI->load->view('web/layout/navigation', $data, TRUE);      $template['mainContent'] = $this->CI->load->view('web/'.$data['view'], $data, TRUE);      $template['footer'] = $this->CI->load->view('web/layout/footer', '', TRUE);      $this->CI->load->view('template', $template);    }    public function adminBase($data) {      $template['header'] = $this->CI->load->view('admin/layout/header', $data, TRUE);      $template['navigation'] = $this->CI->load->view('admin/layout/navigation', $data, TRUE);            $template['sidebarnav'] = $this->CI->load->view('admin/layout/sidebar', $data, TRUE);      $template['mainContent'] = $this->CI->load->view('admin/'.$data['view'], $data, TRUE);      $template['footer'] = $this->CI->load->view('admin/layout/footer', '', TRUE);      $this->CI->load->view('template', $template);    }		  }/* End of file template.php *//* Location: ./application/libraries/template.php */